import React, { useState } from "react";
import * as XLSX from "xlsx";
import {
  addCandidate,
  getCandidates,
  addCandidatesBatch,
} from "../services/candidateService";

function CandidateForm() {
  const [candidateId, setCandidateId] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [typology, setTypology] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [candidates, setCandidates] = useState([]);
  const typologyOptions = ["Studio", "1BR", "2BR", "3BR", "Shop"];

  const fetchCandidates = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getCandidates();
      setCandidates(data);
    } catch (err) {
      setError(err.message || "Failed to fetch candidates");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");
    try {
      const candidateData = { name, email, phone, typology };
      if (candidateId) candidateData.candidateId = candidateId;
      await addCandidate(candidateData);
      setMessage("Candidate added successfully");
      setCandidateId("");
      setName("");
      setEmail("");
      setPhone("");
      setTypology("");
      fetchCandidates();
    } catch (err) {
      setError(err.message || "Failed to add candidate");
    } finally {
      setLoading(false);
    }
  };

  // Batch upload handler
  const handleBatchUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
    setLoading(true);
    setMessage("");
    setError("");
    try {
      const reader = new FileReader();
      reader.onload = async (evt) => {
        try {
          const data = evt.target.result;
          const workbook = XLSX.read(data, { type: "binary" });
          const sheetName = workbook.SheetNames[0];
          const worksheet = workbook.Sheets[sheetName];
          const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: "" });
          // Collect all valid candidates
          const candidatesToAdd = [];
          for (const row of jsonData) {
            if (
              row["Name"] &&
              row["Email"] &&
              row["Phone"] &&
              row["Typology"]
            ) {
              const candidateData = {
                name: row["Name"],
                email: row["Email"],
                phone: row["Phone"],
                typology: row["Typology"],
              };
              if (row["CandidateId"])
                candidateData.candidateId = row["CandidateId"];
              candidatesToAdd.push(candidateData);
            }
          }
          if (candidatesToAdd.length > 0) {
            await addCandidatesBatch(candidatesToAdd);
            setMessage(
              `Batch upload complete: ${candidatesToAdd.length} added, ${
                jsonData.length - candidatesToAdd.length
              } failed.`
            );
          } else {
            setMessage("No valid candidates found in file.");
          }
          fetchCandidates();
        } catch (err) {
          setError("Failed to process file: " + (err.message || err));
        } finally {
          setLoading(false);
        }
      };
      reader.readAsBinaryString(file);
    } catch (err) {
      setError("Failed to read file: " + (err.message || err));
      setLoading(false);
    }
  };

  // Download Excel template
  const handleDownloadTemplate = () => {
    const ws = XLSX.utils.aoa_to_sheet([
      ["CandidateId", "Name", "Email", "Phone", "Typology"],
      ["1", "John Doe", "john@example.com", "0912345678", "1BR"],
      ["2", "Jane Smith", "jane@example.com", "0987654321", "2BR"],
    ]);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Candidates");
    XLSX.writeFile(wb, "candidate_template.xlsx");
  };

  React.useEffect(() => {
    fetchCandidates();
  }, []);

  return (
    <div className="container-card">
      <h2>Add Candidate</h2>
      {message && <p className="message-success">{message}</p>}
      {error && <p className="message-error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          CandidateId (optional):
          <input
            type="number"
            value={candidateId}
            onChange={(e) => setCandidateId(e.target.value)}
            placeholder="Auto-generated if left blank"
          />
        </label>
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </label>
        <label>
          Typology:
          <select
            value={typology}
            onChange={(e) => setTypology(e.target.value)}
            required
          >
            <option value="">Select Typology</option>
            {typologyOptions.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </label>
        <button type="submit" disabled={loading}>
          Add Candidate
        </button>
      </form>
      <div style={{ margin: "20px 0" }}>
        <label>
          Batch Upload Candidates (Excel):
          <input
            type="file"
            accept=".xlsx, .xls"
            onChange={handleBatchUpload}
            disabled={loading}
            style={{ display: "block", marginTop: "8px" }}
          />
        </label>
        <button
          type="button"
          onClick={handleDownloadTemplate}
          style={{ marginLeft: 12 }}
        >
          Download Excel Template
        </button>
        <div style={{ fontSize: "0.95em", color: "#555", marginTop: 4 }}>
          Excel columns required: <b>CandidateId</b> (optional), <b>Name</b>,{" "}
          <b>Email</b>, <b>Phone</b>, <b>Typology</b>
        </div>
      </div>
      <h3>Candidate List</h3>
      <ul>
        {candidates.map((c) => (
          <li key={c.candidateId || c.id || c._id}>
            {c.candidateId ? `#${c.candidateId}: ` : ""}
            {c.name} ({c.email}, {c.phone || "-"}) - {c.typology || "-"}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CandidateForm;
