// frontend/src/pages/AAHDCAdminPage.js
import React, { useState, useEffect } from "react";
import { getUnits } from "../services/unitService";
import { runAllocation, getAllocatedUnits } from "../services/allocationService";
import {
  downloadExcelReport,
  downloadPdfReport,
} from "../services/reportService";

function AAHDCAdminPage() {
  // const [units, setUnits] = useState([]);
  // Allocated units now displayed from allocated snapshot endpoint
  const [unallocatedUnits, setUnallocatedUnits] = useState([]);
  const [allocatedSnapshot, setAllocatedSnapshot] = useState([]); // from /api/allocation/allocated
  const [selectedMethod, setSelectedMethod] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  // Pagination state
  const [unallocatedPage, setUnallocatedPage] = useState(1);
  const [allocatedPage, setAllocatedPage] = useState(1);
  const PAGE_SIZE = 10;

  // const fetchUnits = async () => {
  //   setLoading(true);
  //   try {
  //     const allUnits = await getUnits();
  //     setUnits(allUnits);
  //     setAllocatedUnits(allUnits.filter((u) => u.allocated));
  //     setUnallocatedUnits(allUnits.filter((u) => !u.allocated));
  //   } catch (err) {
  //     setError(err.message || "Failed to fetch units.");
  //   } finally {
  //     setLoading(false);
  //   }
  // };

  const fetchUnits = async () => {
    setLoading(true);
    try {
      const allUnits = await getUnits();
      // Defensive: filter out units missing required fields
      const validUnits = Array.isArray(allUnits)
        ? allUnits.filter(
            (u) =>
              u &&
              typeof u.unitId !== "undefined" &&
              typeof u.allocated !== "undefined"
          )
        : [];
      setAllocatedUnits(validUnits.filter((u) => u.allocated === true));
      setUnallocatedUnits(validUnits.filter((u) => u.allocated === false));
      setUnallocatedPage(1); // Reset to first page on data reload
      setAllocatedPage(1);
    } catch (err) {
      setError(err.message || "Failed to fetch units.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUnits();
  fetchAllocated();
  }, []);

  // const handleRunAllocation = async () => {
  //   setMessage("");
  //   setError("");
  //   setLoading(true);
  //   try {
  //     const response = await runAllocation(selectedMethod);
  //     setMessage(response.message);
  //     if (response.complianceIssues && response.complianceIssues.length > 0) {
  //       setError(
  //         "Compliance issues detected after allocation: " +
  //           response.complianceIssues.join(", ")
  //       );
  //     }
  //     fetchUnits(); // Refresh unit data
  //   } catch (err) {
  //     setError(err.message || "Failed to run allocation.");
  //   } finally {
  //     setLoading(false);
  //   }
  // };
  const handleRunAllocation = async () => {
    setMessage("");
    setError("");
    setLoading(true);
    try {
      const response = await runAllocation(selectedMethod);
      setMessage(response.message);
      if (response.complianceIssues && response.complianceIssues.length > 0) {
        setError(
          "Compliance issues detected after allocation: " +
            response.complianceIssues.join(", ")
        );
      }
    } catch (err) {
      try {
        // Try parsing the error message as JSON for detailed issues
        const errorData = JSON.parse(err.message);
        if (errorData.issues) {
          setError(
            "Pre-allocation compliance failed: " + errorData.issues.join(", ")
          );
        } else {
          setError(err.message || "Failed to run allocation.");
        }
      } catch {
        setError(err.message || "Failed to run allocation.");
      }
    } finally {
      setLoading(false);
      fetchUnits();
      // Refresh allocated snapshot (includes candidate info if present)
      fetchAllocated();
    }
  };

  const fetchAllocated = async () => {
    try {
      const data = await getAllocatedUnits();
      setAllocatedSnapshot(Array.isArray(data) ? data : []);
    } catch (e) {
      // non-fatal for page
    }
  };

  const handleDownloadExcel = async () => {
    setError("");
    setMessage("");
    setLoading(true);
    try {
      await downloadExcelReport();
      setMessage("Excel report downloaded successfully.");
    } catch (err) {
      setError(err.message || "Failed to download Excel report.");
    } finally {
      setLoading(false);
    }
  };

  const handleDownloadPdf = async () => {
    setError("");
    setMessage("");
    setLoading(true);
    try {
      await downloadPdfReport();
      setMessage("PDF report downloaded successfully.");
    } catch (err) {
      setError(err.message || "Failed to download PDF report.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container-card">
      <h2>AAHDC Admin Portal</h2>
      {loading && <p>Loading...</p>}
      {message && <p className="message-success">{message}</p>}
      {error && <p className="message-error">{error}</p>}

      <section style={{ marginBottom: "30px" }}>
        <h3>Unallocated Units ({unallocatedUnits.length})</h3>
        {unallocatedUnits.length > 0 ? (
          <>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                marginTop: "10px",
              }}
            >
              <thead>
                <tr>
                  <th>Unit ID</th>
                  <th>Typology</th>
                  <th>Gross Area</th>
                  <th>Floor</th>
                  <th>Block</th>
                </tr>
              </thead>
              <tbody>
                {unallocatedUnits
                  .slice(
                    (unallocatedPage - 1) * PAGE_SIZE,
                    unallocatedPage * PAGE_SIZE
                  )
                  .map((unit) => (
                    <tr key={unit.unitId}>
                      <td>{unit.unitId}</td>
                      <td>{unit.typology || "-"}</td>
                      <td>
                        {unit.grossArea
                          ? Number(unit.grossArea).toFixed(2)
                          : "-"}
                      </td>
                      <td>{unit.floorNumber || "-"}</td>
                      <td>{unit.blockName || "-"}</td>
                    </tr>
                  ))}
              </tbody>
            </table>
            {/* Pagination controls */}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                marginTop: 10,
                gap: 10,
              }}
            >
              <button
                onClick={() => setUnallocatedPage((p) => Math.max(1, p - 1))}
                disabled={unallocatedPage === 1}
              >
                Previous
              </button>
              <span>
                Page {unallocatedPage} of{" "}
                {Math.ceil(unallocatedUnits.length / PAGE_SIZE)}
              </span>
              <button
                onClick={() =>
                  setUnallocatedPage((p) =>
                    p < Math.ceil(unallocatedUnits.length / PAGE_SIZE)
                      ? p + 1
                      : p
                  )
                }
                disabled={
                  unallocatedPage >=
                  Math.ceil(unallocatedUnits.length / PAGE_SIZE)
                }
              >
                Next
              </button>
            </div>
          </>
        ) : (
          <p>
            No unallocated units to display. Please submit units via the
            Developer Portal.
          </p>
        )}
      </section>

      <section
        style={{
          marginBottom: "30px",
          borderTop: "1px solid #eee",
          paddingTop: "20px",
        }}
      >
        <h3>Run Allocation</h3>
        <label
          style={{ display: "block", marginBottom: "15px", textAlign: "left" }}
        >
          Select Distribution Method:
          <select
            value={selectedMethod}
            onChange={(e) => setSelectedMethod(e.target.value)}
            style={{
              width: "100%",
              padding: "10px",
              borderRadius: "8px",
              border: "1px solid #ddd",
              marginTop: "5px",
            }}
          >
            <option value="">-- Select Method --</option>
            <option value="Full Lottery">Full Lottery (Houses + Shops)</option>
            <option value="Hybrid Lottery">
              Hybrid Lottery (Houses) + Negotiation (Shops)
            </option>
            <option value="Block-by-Block Assignment">
              Block-by-Block Assignment
            </option>
            <option value="Lottery Based on Floor Number">
              Lottery Based on Floor Number
            </option>
            <option value="Assign Candidates by Typology">
              Assign Candidates by Typology (match candidates to units)
            </option>
          </select>
        </label>
        <button
          onClick={handleRunAllocation}
          disabled={!selectedMethod || loading || unallocatedUnits.length === 0}
        >
          Run Allocation
        </button>
      </section>

      <section
        style={{
          marginBottom: "30px",
          borderTop: "1px solid #eee",
          paddingTop: "20px",
        }}
      >
  <h3>Allocated Units ({allocatedSnapshot.length})</h3>
  {allocatedSnapshot.length > 0 ? (
          <>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                marginTop: "10px",
              }}
            >
              <thead>
                <tr>
                  <th>Unit ID</th>
                  <th>Typology</th>
                  <th>Gross Area</th>
                  <th>Floor</th>
                  <th>Block</th>
                  <th>Owner / Candidate</th>
                  <th>Phone</th>
                </tr>
              </thead>
              <tbody>
                {allocatedSnapshot
                  .slice(
                    (allocatedPage - 1) * PAGE_SIZE,
                    allocatedPage * PAGE_SIZE
                  )
                  .map((row) => (
                    <tr key={row.unitId}>
                      <td>{row.unitId}</td>
                      <td>{row.typology || "-"}</td>
                      <td>
                        {row.grossArea
                          ? Number(row.grossArea).toFixed(2)
                          : "-"}
                      </td>
                      <td>{row.floorNumber || "-"}</td>
                      <td>{row.blockName || "-"}</td>
                      <td
                        style={{
                          fontWeight: "bold",
                          color: row.owner === "AAHDC" ? "#27ae60" : "#2980b9",
                        }}
                      >
                        {row.owner || row.candidateName || "-"}
                      </td>
                      <td style={{ color: "#333" }}>
                        {row.candidatePhone || "-"}
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
            {/* Pagination controls */}
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                marginTop: 10,
                gap: 10,
              }}
            >
              <button
                onClick={() => setAllocatedPage((p) => Math.max(1, p - 1))}
                disabled={allocatedPage === 1}
              >
                Previous
              </button>
              <span>
        Page {allocatedPage} of {Math.ceil(allocatedSnapshot.length / PAGE_SIZE)}
              </span>
              <button
                onClick={() =>
                  setAllocatedPage((p) =>
          p < Math.ceil(allocatedSnapshot.length / PAGE_SIZE) ? p + 1 : p
                  )
                }
        disabled={allocatedPage >= Math.ceil(allocatedSnapshot.length / PAGE_SIZE)}
              >
                Next
              </button>
            </div>
          </>
        ) : (
          <p>
            No units have been allocated yet. Run an allocation method above.
          </p>
        )}
      </section>

      <section style={{ borderTop: "1px solid #eee", paddingTop: "20px" }}>
        <h3>Reports</h3>
        <div style={{ display: "flex", justifyContent: "center", gap: "15px" }}>
          <button
            onClick={handleDownloadExcel}
            disabled={loading || allocatedSnapshot.length === 0}
            style={{ backgroundColor: "#28a745" }}
          >
            Download Excel Report
          </button>
          <button
            onClick={handleDownloadPdf}
            disabled={loading || allocatedSnapshot.length === 0}
            style={{ backgroundColor: "#6c757d" }}
          >
            Download PDF Report
          </button>
        </div>
      </section>
    </div>
  );
}

export default AAHDCAdminPage;
