import React, { useState } from "react";
// ...removed XLSX usage; backend handles parsing...
import {
  addCandidate,
  getCandidates,
  importCandidates,
} from "../services/candidateService";

function CandidateForm() {
  const [candidateId, setCandidateId] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [typology, setTypology] = useState("");
  const [message, setMessage] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [candidates, setCandidates] = useState([]);
  const typologyOptions = ["Studio", "1BR", "2BR", "3BR", "Shop"];

  const fetchCandidates = async () => {
    setLoading(true);
    setError("");
    try {
      const data = await getCandidates();
      setCandidates(data);
    } catch (err) {
      setError(err.message || "Failed to fetch candidates");
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setError("");
    try {
      const candidateData = { name, email, phone, typology };
      if (candidateId) candidateData.candidateId = candidateId;
      await addCandidate(candidateData);
      setMessage("Candidate added successfully");
      setCandidateId("");
      setName("");
      setEmail("");
      setPhone("");
      setTypology("");
      fetchCandidates();
    } catch (err) {
      setError(err.message || "Failed to add candidate");
    } finally {
      setLoading(false);
    }
  };

  // Batch upload handler (use backend to parse & import)
  const handleBatchUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;
    setLoading(true);
    setMessage("");
    setError("");
    try {
      const result = await importCandidates(file);
      const {
        importedCount = 0,
        failedRows = [],
        errorDetails = [],
      } = result || {};
      if (importedCount > 0) {
        setMessage(
          `Batch upload complete: ${importedCount} added${
            failedRows.length ? `, ${failedRows.length} failed` : ""
          }.`
        );
      } else if (failedRows.length || errorDetails.length) {
        setError(
          `No valid candidates found. ${
            errorDetails.length ? `Details: ${errorDetails.join("; ")}` : ""
          }`
        );
      } else {
        setMessage("No valid candidates found in file.");
      }
      fetchCandidates();
    } catch (err) {
      setError(err.message || "Failed to import candidates");
    } finally {
      setLoading(false);
    }
  };

  // Download Excel template (from backend)
  const handleDownloadTemplate = async () => {
    try {
      const res = await fetch("http://localhost:5000/api/candidates/template");
      if (!res.ok) throw new Error("Failed to download template");
      const blob = await res.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = "candidate_template.xlsx";
      document.body.appendChild(a);
      a.click();
      a.remove();
      window.URL.revokeObjectURL(url);
    } catch (e) {
      setError(e.message || "Failed to download template");
    }
  };

  React.useEffect(() => {
    fetchCandidates();
  }, []);

  return (
    <div className="container-card">
      <h2>Add Candidate</h2>
      {message && <p className="message-success">{message}</p>}
      {error && <p className="message-error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <label>
          CandidateId (optional):
          <input
            type="number"
            value={candidateId}
            onChange={(e) => setCandidateId(e.target.value)}
            placeholder="Auto-generated if left blank"
          />
        </label>
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <label>
          Phone:
          <input
            type="text"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            required
          />
        </label>
        <label>
          Typology:
          <select
            value={typology}
            onChange={(e) => setTypology(e.target.value)}
            required
          >
            <option value="">Select Typology</option>
            {typologyOptions.map((type) => (
              <option key={type} value={type}>
                {type}
              </option>
            ))}
          </select>
        </label>
        <button type="submit" disabled={loading}>
          Add Candidate
        </button>
      </form>
      <div style={{ margin: "20px 0" }}>
        <label>
          Batch Upload Candidates (Excel):
          <input
            type="file"
            accept=".xlsx, .xls, .csv"
            onChange={handleBatchUpload}
            disabled={loading}
            style={{ display: "block", marginTop: "8px" }}
          />
        </label>
        <button
          type="button"
          onClick={handleDownloadTemplate}
          style={{ marginLeft: 12 }}
        >
          Download Excel Template
        </button>
        <div style={{ fontSize: "0.95em", color: "#555", marginTop: 4 }}>
          Tip: Use the Download Excel Template to ensure correct headers.
        </div>
      </div>
      <h3>Candidate List</h3>
      {candidates && candidates.length > 0 ? (
        <div style={{ overflowX: "auto" }}>
          <table
            style={{ width: "100%", borderCollapse: "collapse", marginTop: 8 }}
          >
            <thead>
              <tr>
                <th style={{ textAlign: "left", padding: 8 }}>ID</th>
                <th style={{ textAlign: "left", padding: 8 }}>Name</th>
                <th style={{ textAlign: "left", padding: 8 }}>Email</th>
                <th style={{ textAlign: "left", padding: 8 }}>Phone</th>
                <th style={{ textAlign: "left", padding: 8 }}>Typology</th>
                <th style={{ textAlign: "left", padding: 8 }}>Assigned Unit</th>
              </tr>
            </thead>
            <tbody>
              {candidates.map((c) => (
                <tr key={c.candidateId || c.id || c._id}>
                  <td style={{ padding: 8 }}>{c.candidateId || c.id || "-"}</td>
                  <td style={{ padding: 8 }}>{c.name || "-"}</td>
                  <td style={{ padding: 8 }}>{c.email || "-"}</td>
                  <td style={{ padding: 8 }}>{c.phone || "-"}</td>
                  <td style={{ padding: 8 }}>{c.typology || "-"}</td>
                  <td style={{ padding: 8 }}>{c.assignedUnitId || "-"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ) : (
        <p style={{ color: "#555" }}>No candidates found.</p>
      )}
    </div>
  );
}

export default CandidateForm;
